"use strict";function initMap(){var o=new google.maps.Map(document.getElementById("map"),{zoom:15,center:{lat:6.459964,lng:7.548949}}),t=new google.maps.Geocoder;document.getElementById("submit").addEventListener("click",function(e){e.preventDefault(),geocodeAddress(t,o)})}function geocodeAddress(e,t){var o=document.getElementById("address").value;e.geocode({address:o},function(e,o){if("OK"===o){t.setCenter(e[0].geometry.location);new google.maps.Marker({map:t,position:e[0].geometry.location})}else alert("Geocode was not successful for the following reason: "+o)})}